export let getDefaultStyleForElement = function(card, type) {
  let cardObjectFields = getCardObjectFields(card);
  if (card[cardObjectFields.StylingConfiguration]) {
    if (typeof card[cardObjectFields.StylingConfiguration] === "string") {
      card[cardObjectFields.StylingConfiguration] = JSON.parse(
        card[cardObjectFields.StylingConfiguration]
      );
    }
    let styleDefaults =
      card[cardObjectFields.StylingConfiguration].styleDefaults || {};
    let savedStyleObjects =
      card[cardObjectFields.StylingConfiguration].styleObjects || {};

    if (styleDefaults[type]) {
      return savedStyleObjects[styleDefaults[type]];
    }
  }
  return null;
};
