import {
  getResourceUrl,
  isCommunity,
  getCommunityName
} from "c/salesforceUtils";
import { loadStyle } from "lightning/platformResourceLoader";

let geturl = (responses, path) => {
  // url will not be transformed if not used in Communities
  let url = responses[0] + path;
  const bCommunity = responses[1];
  const communityName = responses[2];

  // For Communities preview
  if (
    bCommunity &&
    (/livepreview/.test(window.location.host) ||
      /live-preview/.test(window.location.host))
  ) {
    url = `/sfsites/c${url}`;
  }
  // For activated Communities
  else if (bCommunity && communityName) {
    url = `/${communityName}${url}`;
  }
  return url;
};

const loadCssFromStaticResource = (context, staticResourceName, path) => {
  return Promise.all([
    getResourceUrl({ resourceName: staticResourceName }),
    isCommunity(),
    getCommunityName()
  ]).then(responses => {
    // url will not be transformed if not used in Communities
    let url = geturl(responses, path);

    return loadStyle(context, url);
  });
};

export { loadCssFromStaticResource };
