import Icon from "c/icon";
import { BaseFlexElementMixin } from "c/baseFlexElementMixin";
import { api } from "lwc";

export default class FlexIcon extends BaseFlexElementMixin(Icon) {
  @api get record() {
    return this._record;
  }
  set record(val) {
    this._record = val;
    this.triggerRender();
  }

  triggerRender() {
    if (
      this.uninterpolatedFields &&
      (this.uninterpolatedFields.iconName ||
        this.uninterpolatedFields.imgsrc ||
        this.uninterpolatedFields.extraclass)
    ) {
      if (this.uninterpolatedFields.extraclass) {
        this._extraclass = this.interpolateValue(
          this.uninterpolatedFields.extraclass
        );
      }
      if (this.uninterpolatedFields.imgsrc) {
        this._imgsrc = this.interpolateValue(this.uninterpolatedFields.imgsrc);
      } else {
        this._iconName = this.interpolateValue(
          this.uninterpolatedFields.iconName,
          "iconName"
        );
        this.updateValue();
      }
    }
  }
}
