import { api, track } from "lwc";
import Menu from "c/menu";
import { BaseFlexElementMixin } from "c/baseFlexElementMixin";

export default class FlexMenu extends BaseFlexElementMixin(Menu) {
  _regexPattern = /\{([a-zA-Z.0-9_]+)\}/g;
  @api get record() {
    return this._record;
  }
  set record(val) {
    if (typeof val !== "undefined") {
      this._record = val;
      this.triggerRender();
    }
  }
  @track _record;

  triggerRender() {}
}
