import { api } from "lwc";
import Toggle from "c/toggle";
import { BaseFlexElementMixin } from "c/baseFlexElementMixin";

export default class FlexToggle extends BaseFlexElementMixin(Toggle) {
  @api get record() {
    return this._record;
  }
  set record(val) {
    this._record = val;
    if (this._rawFieldValues) {
      if (this.isMergeFieldVal(this._rawFieldValues.checked)) {
        this.updateValue(this._rawFieldValues.checked, "checked");
      }
      if (this.isMergeFieldVal(this._rawFieldValues.value)) {
        this.updateValue(this._rawFieldValues.value, "value");
      }
      if (this.isMergeFieldVal(this._rawFieldValues.disabled)) {
        this.disabled = this._rawFieldValues.disabled;
      }
    }
    this.triggerRender();
  }

  isMergeFieldVal(val) {
    return val && typeof val === "string" && val.indexOf("{") !== -1;
  }

  triggerRender() {
    if (this._rawFieldValues?.toggleEnabledLabel) {
      this._toggleEnabledLabel = this.interpolateValue(
        this._rawFieldValues.toggleEnabledLabel
      );
    }
    if (this._rawFieldValues?.toggleDisabledLabel) {
      this._toggleDisabledLabel = this.interpolateValue(
        this._rawFieldValues.toggleDisabledLabel
      );
    }
    if (this._rawFieldValues?.options) {
      this.options = this._rawFieldValues.options;
    }
  }
}
