import { createElement } from "lwc";
import accordion from "c/accordion";
import accordionSection from "c/accordionSection";
import List from "c/list";

describe("c-accordion", () => {
  it("renders DOM elements", () => {
    const element = createElement("c-accordion", {
      is: accordion
    });
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });
  it("renders accordion section", () => {
    const element = createElement("c-accordion", {
      is: accordion
    });

    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      // eslint-disable-next-line @lwc/lwc/no-async-operation
      window.setTimeout(function() {
        const section = createElement("c-accordion-section", {
          is: accordionSection
        });
        const list = createElement("c-list", { is: List });
        list.records = [
          { id: "5b6d2b7e2c084ab095ae12bd", Name: "Grant" },
          { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Shields" },
          { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
        ];
        const ele = element.shadowRoot.querySelectorAll("c-accordion");
        ele[0].appendChild(section);
        element.shadowRoot
          .querySelectorAll("c-accordion-section")[0]
          .appendChild(list);
        expect(element.shadowRoot.querySelectorAll("c-list").length()).toBe(1);
      });
    });
  });
  it("checks for open accordion section", () => {
    const element = createElement("c-accordion", {
      is: accordion
    });
    element.activeSectionName = "A";
    element.theme = "slds";

    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      // eslint-disable-next-line @lwc/lwc/no-async-operation
      window.setTimeout(function() {
        const section = createElement("c-accordion-section", {
          is: accordionSection
        });
        section.Name = "A";
        const section2 = createElement("c-accordion-section", {
          is: accordionSection
        });
        section.Name = "B";
        const list = createElement("c-list", { is: List });
        list.records = [
          { id: "5b6d2b7e2c084ab095ae12bd", Name: "Grant" },
          { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Shields" },
          { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
        ];
        const ele = element.shadowRoot.querySelectorAll("c-accordion");
        ele[0].appendChild(section);
        ele[0].appendChild(section2);
        element.shadowRoot
          .querySelectorAll("c-accordion-section")[0]
          .appendChild(list);
        expect(
          element.shadowRoot.querySelectorAll(".slds-is-open").length()
        ).toBe(1);
      });
    });
  });
});
