import {
  createElement
} from "lwc";
import Button from "c/button";

describe("c-button", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-button", {
      is: Button
    });
    element.iconName = "utility:close";
    element.theme = "slds";
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Has theme set", () => {
    const element = createElement("c-button", {
      is: Button
    });
    element.theme = "slds";
    element.iconName = "utility:connected_apps";
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector("button");
    let index = button.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the element disabled */
  it("Check Is disabled", () => {
    const element = createElement("c-button", {
      is: Button
    });
    element.iconName = "utility:close";
    element.theme = "slds";
    element.disabled = "true";
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector("button");
    expect(button.disabled).toBe(true);
  });

  /* Check if the variant disabled */
  it("Check Is variant set", () => {
    const element = createElement("c-button", {
      is: Button
    });
    element.iconName = "utility:close";
    element.theme = "slds";
    element.disabled = "true";
    element.variant = "brand";
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector("button");
    let index =
      button.className.indexOf("slds-button_brand") !== -1 ? true : false;
    expect(index).toBe(true);
  });
});
