import { createElement } from "lwc";
import Carousel from "c/carousel";

describe("c-carousel", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-carousel", {
      is: Carousel
    });
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  it("Has interval set", () => {
    jest.useFakeTimers();
    const element = createElement("c-carousel", {
      is: Carousel
    });
    element.interval = "100";
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      jest.advanceTimersByTime(500);
      expect(element.shadowRoot.querySelector("c-icon").iconName).toEqual(
        "utility:pause"
      );
    });
  });

  it("Check pause button works", () => {
    jest.useFakeTimers();
    const element = createElement("c-carousel", {
      is: Carousel
    });
    element.interval = "6000";
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      element.shadowRoot.querySelector(".slds-button").click();
      jest.advanceTimersByTime(1000);
      expect(element.shadowRoot.querySelector("c-icon").iconName).toEqual(
        "utility:pause"
      );
    });
  });
});
