import { createElement } from "lwc";
import CarouselItem from "c/carouselItem";

describe("c-carousel-item", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-carousel-item", {
      is: CarouselItem
    });
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  it("Has header set", () => {
    const element = createElement("c-carousel-item", {
      is: CarouselItem
    });
    element.header = "Test Header";
    document.body.appendChild(element);
    expect(
      element.shadowRoot.querySelector(".slds-carousel__content h2").textContent
    ).toEqual("Test Header");
  });

  it("Has description set", () => {
    const element = createElement("c-carousel-item", {
      is: CarouselItem
    });
    element.description = "Test Description";
    document.body.appendChild(element);
    expect(
      element.shadowRoot.querySelector(".slds-carousel__content p").textContent
    ).toEqual("Test Description");
  });
});
