import { LightningElement, api, track } from "lwc";

export default class CarouselItem extends LightningElement {
  @api thumbnail;
  @api src;
  @api header;
  @api description;
  @api alternativeText;
  @api href;
  @api extraclass = "";

  @track isbaseversion;

  connectedCallback() {
    this.isbaseversion = true;
    this.classList.add("slds-carousel__panel");
  }

  renderedCallback() {
    this.isbaseversion = this.querySelector("*:nth-child(1)") ? false : true;
  }

  get carouselItemClass() {
    return `slds-text-link_reset ${this.extraclass}`;
  }
}
