import { createElement } from "lwc";
import DataTableCell from "c/dataTableCell";

describe("c-data-table-cell", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-data-table-cell", {
      is: DataTableCell
    });
    element.columnData = {
      fieldName: "email",
      label: "Email",
      sortable: true,
      searchable: true,
      type: "email",
      editable: true,
      userSelectable: true
    };
    element.cellData = {
      editable: true,
      label: "Email",
      value: "agupta@vlocity.com"
    };
    document.body.appendChild(element);

    element.shadowRoot.querySelector("div").dispatchEvent(
      new CustomEvent("mouseenter", {
        bubbles: true,
        composed: true
      })
    );
    element.shadowRoot.querySelector("div").dispatchEvent(
      new CustomEvent("mouseleave", {
        bubbles: true,
        composed: true
      })
    );
    element.shadowRoot.querySelector("c-icon").click();

    expect(element.shadowRoot.children).not.toHaveLength(0);
  });
});
