import { createElement } from "lwc";
import Icon from "c/icon";

describe("c-icon", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-icon", {
      is: Icon
    });
    element.icon = "utility:close";
    element.theme = "slds";
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Has theme set", () => {
    const element = createElement("c-icon", {
      is: Icon
    });
    element.theme = "slds";
    element.icon = "utility:connected_apps";
    document.body.appendChild(element);
    const svg = element.shadowRoot.querySelector("svg");
    expect(svg.getAttribute("class")).toContain("slds");
    expect(svg.getAttribute("class")).not.toContain("nds");
  });

  /**
   * Check if the size is applied properly
   */
  it("Has size set", () => {
    const element = createElement("c-icon", {
      is: Icon
    });
    element.theme = "slds";
    element.size = "medium";
    element.icon = "utility:connected_apps";
    document.body.appendChild(element);
    const svg = element.shadowRoot.querySelector("svg");
    expect(svg.classList).toContain("slds-icon_medium");
  });
});
