import {
  createElement
} from 'lwc';
import Layout from 'c/layout';

describe('c-layout', () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it('renders DOM elements', () => {
    const element = createElement('c-layout', {
      is: Layout
    });
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  it('Has nds theme', () => {
    const element = createElement('c-layout', {
      is: Layout
    });
    element.theme = "nds";
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.querySelector("div").className).toContain("nds");
      expect(element.shadowRoot.querySelector("div").className).not.toContain("slds");
    });
  });

});
