import { createElement } from "lwc";
import Layout from "c/layoutItem";

describe("c-layout-item", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-layout-item", {
      is: Layout
    });
    element.size = "3";
    element.smallDeviceSize = "12";
    element.mediumDeviceSize = "4";
    element.largeDeviceSize = "3";
    element.alignmentBump = "left";
    element.flexibility = "shrink";
    element.padding = "horizontal-small";
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });
});
