import { createElement } from "lwc";
import List from "c/list";

describe("c-list", () => {
  describe("with data", () => {
    it("renders DOM elements", () => {
      const element = createElement("c-list", { is: List });
      element.records = [
        { id: "5b6d2b7e2c084ab095ae12bd", Name: "Grant" },
        { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Shields" },
        { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
      ];
      document.body.appendChild(element);
      expect(element.shadowRoot.children).not.toHaveLength(0);
    });
  });

  it("Has list created", () => {
    const element = createElement("c-list", {
      is: List
    });
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Grant" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Shields" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
    ];

    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      const data = element.shadowRoot.querySelectorAll("li");
      expect(data).toHaveLength(element.records.length);
    });
  });

  it("Check if sort function is available", () => {
    const element = createElement("c-list", {
      is: List
    });
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Shields" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Grant" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
    ];
    element.isSortAvailable = true;
    document.body.appendChild(element);
    const list = element.shadowRoot.querySelector("button");
    list.dispatchEvent(new Event("click"));
    return Promise.resolve().then(() => {
      const data = element.shadowRoot.querySelectorAll("li")[0];
      expect(data.textContent).toBe("Grant");
    });
  });

  it("Check if search function is available", () => {
    const element = createElement("c-list", {
      is: List
    });
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Shields" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Grant" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
    ];
    element.isSearchAvailable = true;
    document.body.appendChild(element);
    // eslint-disable-next-line @lwc/lwc/no-async-operation
    setTimeout(() => {
      const input = element.shadowRoot.querySelector(".searchInput");
      input.value = "V";
      input.dispatchEvent(new Event("keyup"));
      return Promise.resolve().then(() => {
        const searchData = element.shadowRoot.querySelectorAll("li");
        expect(searchData).toHaveLength(1);
        expect(searchData[0].textContent).toBe("Valdez");
      });
    }, 0);
  });

  it("Check if sort attribute is set", () => {
    const element = createElement("c-list", {
      is: List
    });
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Shields", Color: "Red" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Grant", Color: "Yellow" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez", Color: "Blue" }
    ];
    element.isSortAvailable = true;
    element.sortAttribute = "Color";
    document.body.appendChild(element);
    const list = element.shadowRoot.querySelector("button");
    list.dispatchEvent(new Event("click"));
    return Promise.resolve().then(() => {
      const data = element.shadowRoot.querySelectorAll("li")[0];
      expect(data.textContent).toBe("Valdez");
    });
  });

  it("Check if search attribute is set", () => {
    const element = createElement("c-list", {
      is: List
    });
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Shields", Color: "Red" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Grant", Color: "Yellow" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez", Color: "Blue" }
    ];
    element.isSearchAvailable = true;
    element.searchAttribute = "Color";
    document.body.appendChild(element);
    // eslint-disable-next-line @lwc/lwc/no-async-operation
    setTimeout(() => {
      const input = element.shadowRoot.querySelector(".searchInput");
      input.value = "Y";
      input.dispatchEvent(new Event("keyup"));
      return Promise.resolve().then(() => {
        const searchData = element.shadowRoot.querySelectorAll("li");
        expect(searchData).toHaveLength(1);
        expect(searchData[0].textContent).toBe("Grant");
      });
    }, 0);
  });

  it("Check if fieldName attribute is set", () => {
    const element = createElement("c-list", {
      is: List
    });
    element.fieldName = "Contact";
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Contact: "Shields" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Contact: "Grant" },
      { id: "5b6d2b7e1014cfa827849ab7", Contact: "Valdez" }
    ];
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      const data = element.shadowRoot.querySelectorAll("li");
      expect(data[0].textContent).toBe("Shields");
    });
  });

  it("Check if list height is set", () => {
    const element = createElement("c-list", {
      is: List
    });
    element.height = "300px";
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Shields" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Grant" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
    ];
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      const contentEl = element.shadowRoot.querySelector("ul").parentNode;
      expect(contentEl.style.height).toBe("300px");
    });
  });
});
