import { createElement } from "lwc";
import ListItems from "c/listItems";

describe("c-list-Items", () => {
  describe("with data", () => {
    it("renders DOM elements", () => {
      const element = createElement("c-list-Items", { is: ListItems });
      element.records = [
        { id: "5b6d2b7e2c084ab095ae12bd", Name: "Grant" },
        { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Shields" },
        { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
      ];
      element.theme = "slds";
      document.body.appendChild(element);
      expect(element.shadowRoot.children).not.toHaveLength(0);
    });
  });

  it("Check if theme is set", () => {
    const element = createElement("c-list-Items", { is: ListItems });
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Grant" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Shields" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
    ];
    element.theme = "slds";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector("ul");
    let index = ele.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  it("Has list created", () => {
    const element = createElement("c-list-Items", {
      is: ListItems
    });
    element.records = [
      { id: "5b6d2b7e2c084ab095ae12bd", Name: "Grant" },
      { id: "5b6d2b7ed9ff640dc32b24d4", Name: "Shields" },
      { id: "5b6d2b7e1014cfa827849ab7", Name: "Valdez" }
    ];
    element.theme = "slds";
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      const data = element.shadowRoot.querySelectorAll("li");
      expect(data).toHaveLength(element.records.length);
    });
  });
});
