import { createElement } from "lwc";
import Menu from "c/menu";

describe("c-menu", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-menu", {
      is: Menu
    });
    element.size = "large";
    element.theme = "slds";
    element.position = "right";
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Check if theme is set", () => {
    const element = createElement("c-menu", {
      is: Menu
    });
    element.size = "large";
    element.theme = "slds";
    element.position = "right";
    element.hideslot = "true";
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector("div");
    let index = button.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the size is set */
  it("Check If size is set", () => {
    const element = createElement("c-menu", {
      is: Menu
    });
    element.size = "large";
    element.theme = "slds";
    element.position = "right";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector(".slds-dropdown_large");
    let index = ele ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the position is set */
  it("Check If position is set", () => {
    const element = createElement("c-menu", {
      is: Menu
    });
    element.size = "large";
    element.theme = "slds";
    element.position = "right";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector(".slds-dropdown_right");
    let index = ele ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the overflow scroll is set */
  it("Check If overflow scroll is set", () => {
    const element = createElement("c-menu", {
      is: Menu
    });
    element.size = "large";
    element.theme = "slds";
    element.overflow = true;
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector(".slds-dropdown_length-5");
    let index = ele ? true : false;
    expect(index).toBe(true);
  });

  /* Check if action is set */
  it("Check If action is set", () => {
    const element = createElement("c-menu", {
      is: Menu
    });
    element.size = "large";
    element.theme = "slds";
    element.overflow = true;
    element.type = "action";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector(".slds-dropdown_actions");
    let index = ele ? true : false;
    expect(index).toBe(true);
    expect(element.value).toBe(null);
  });
});
