import {
  createElement
} from "lwc";
import MenuItem from "c/menuItem";

describe("c-menu-item", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-menu-item", {
      is: MenuItem
    });
    element.record = { id: 1, Name: "Menu 1" };
    element.theme = "slds";
    element.iconName = "action:info";
    element.checked = true;
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Check if theme is set", () => {
    const element = createElement("c-menu-item", {
      is: MenuItem
    });
    element.record = { id: 1, Name: "Menu 1" };
    element.theme = "slds";
    element.iconName = "action:info";
    element.checked = true;
    document.body.appendChild(element);
    const li = element.shadowRoot.querySelector("li");
    let index = li.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the item is checked*/
  it("Check if the item is checked", () => {
    const element = createElement("c-menu-item", {
      is: MenuItem
    });
    element.record = { id: 1, Name: "Menu 1" };
    element.theme = "slds";
    element.iconName = "action:info";
    element.checked = true;
    document.body.appendChild(element);
    const li = element.shadowRoot.querySelector("li");
    let index = li.className.indexOf("slds-is-selected") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the header is set*/
  it("Check if the header is set", () => {
    const element = createElement("c-menu-item", {
      is: MenuItem
    });
    element.record = { id: 1, Name: "Menu 1" };
    element.theme = "slds";
    element.iconName = "action:info";
    element.checked = true;
    element.type = "header";
    document.body.appendChild(element);
    const li = element.shadowRoot.querySelector("li");
    let index = li.className.indexOf("slds-dropdown__header") !== -1 ? true : false;
    expect(index).toBe(true);
  });
});
