import { createElement } from "lwc";
import NavigateAction from "c/navigateAction";
import { ComponentPageReference } from "c/utility";

const NAME = "Component";
NavigateAction.prototype.generateUrl = jest.fn().mockResolvedValue("test.url");

describe("c-navigate-action", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("Comparing navigateAction component pageReference object", () => {
    const element = createElement("c-navigate-action", {
      is: NavigateAction
    });

    element.targetType = NAME;
    element.targetName = NAME;
    element.targetParams = "";
    element.targetId = "007";
    document.body.appendChild(element);

    let PageReferenceEl = new ComponentPageReference(NAME, "");
    return element.navigate().then(pageRef => {
      let isEqual = JSON.stringify(pageRef) === JSON.stringify(PageReferenceEl);
      expect(isEqual).toBe(true);
    });
  });
});
