import { createElement } from "lwc";
import OutputField from "c/outputField";

describe("c-output-field", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-output-field", {
      is: OutputField
    });
    element.record = { name: "John", email: "john@develop.com" };
    element.fieldName = "email";
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.textContent).toBe("john@develop.com");
    });
  });
});
