import { createElement } from "lwc";
import VlocityPrompt from "c/prompt";

describe("c-prompt", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-prompt", {
      is: VlocityPrompt
    });
    element.message = "This is the small alert!";
    element.theme = "slds";
    element.title = "Warning";
    document.body.appendChild(element);
    element.openPrompt();
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.children).not.toHaveLength(0);
    });
  });

  /**
   * Check if the theme is applied properly
   */
  it("Check if modal opens", () => {
    const element = createElement("c-prompt", {
      is: VlocityPrompt
    });
    element.message = "This is the small alert!";
    element.theme = "slds";
    element.title = "Warning";
    document.body.appendChild(element);
    element.openPrompt();
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.querySelector("[slot='header']")).not.toBe(
        null
      );
    });
  });

  /* Check if the title disabled */

  it("Check If title is set", () => {
    const element = createElement("c-prompt", {
      is: VlocityPrompt
    });
    element.message = "This is the small alert!";
    element.theme = "nds";
    element.title = "Warning";
    document.body.appendChild(element);
    element.openPrompt();
    return Promise.resolve().then(() => {
      const title = element.shadowRoot.querySelector("h1");
      expect(title.textContent).toBe("Warning");
    });
  });

  /* Check if the theme disabled */
  it("Check If theme is set", () => {
    const element = createElement("c-prompt", {
      is: VlocityPrompt
    });
    element.message = "This is the small alert!";
    element.theme = "nds";
    element.title = "Warning";
    document.body.appendChild(element);
    element.openPrompt();
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.querySelector("div").className).toContain(
        "nds"
      );
      expect(element.shadowRoot.querySelector("div").className).not.toContain(
        "slds"
      );
    });
  });
});
