import { createElement } from "lwc";
import VlocityRadioGroup from "c/radioGroup";

describe("c-radio-group", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-radio-group", {
      is: VlocityRadioGroup
    });
    element.options = [
      {
        label: "Sales",
        value: "option1"
      },
      {
        label: "Force",
        value: "option2"
      }
    ];
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Check if theme is set", () => {
    const element = createElement("c-radio-group", {
      is: VlocityRadioGroup
    });
    element.theme = "slds";
    element.name = "tg1";
    element.options = [
      {
        label: "Sales",
        value: "option1"
      },
      {
        label: "Force",
        value: "option2"
      }
    ];
    element.label = "Select option";
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector("label");
    let index = button.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the label is set */
  it("Check If options label is set", () => {
    const element = createElement("c-radio-group", {
      is: VlocityRadioGroup
    });
    element.theme = "slds";
    element.name = "tg1";
    element.options = [
      {
        label: "Sales",
        value: "option1"
      },
      {
        label: "Force",
        value: "option2"
      }
    ];
    element.label = "Select option";
    document.body.appendChild(element);
    const label = element.shadowRoot.querySelector("label");
    expect(label.textContent).toBe("Sales");
  });

  /* Check if the value is set */
  it("Check If value is set", () => {
    const element = createElement("c-radio-group", {
      is: VlocityRadioGroup
    });
    element.theme = "slds";
    element.name = "tg1";
    element.options = [
      {
        label: "Sales",
        value: "option1"
      },
      {
        label: "Force",
        value: "option2"
      }
    ];
    element.value = "option1";
    element.label = "Select option";
    document.body.appendChild(element);
    const input = element.shadowRoot.querySelector("input");
    expect(input.value).toBe("option1");
  });

  /* Check if the required attribute is set */
  it("Check If required attribute is set", () => {
    const element = createElement("c-radio-group", {
      is: VlocityRadioGroup
    });
    element.theme = "slds";
    element.name = "tg1";
    element.options = [
      {
        label: "Sales",
        value: "option1"
      },
      {
        label: "Force",
        value: "option2"
      }
    ];
    element.required = true;
    element.label = "Select option";
    document.body.appendChild(element);
    expect(element.checkValidity()).toBe(false);
  });
});
