/**
 * The RouteConfig Interface describes a specific route in your application.
 */
interface RouteConfig {
  /**
   * The value of title will be used to update the documents title.
   */
  title: string;
  /**
   * The value of the load property must return an import
   * statement. The value of the import statement is `namespace/componentName` identifier
   * of a component in your application. The value of the import statement MUST be
   * a string literal as it myst be statically analyzed by the LWC compiler.
   *
   * @example
   * ``` JS
   * // A typical implementation:
   * load: () => import('c/homeComponent')
   * ```
   */
  load: Function;
}

interface RouteChangeEvent extends CustomEvent {
  type: "routechange";
}
