import { createElement } from "lwc";
import VlocityTabs from "c/tabset";

describe("c-tabset", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-tabset", {
      is: VlocityTabs
    });
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Check if theme is set", () => {
    const element = createElement("c-tabset", {
      is: VlocityTabs
    });
    element.theme = "slds";
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector("div");
    let index = button.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the variant disabled */
  it("Check If index is set", () => {
    const element = createElement("c-tabset", {
      is: VlocityTabs
    });
    element.theme = "slds";
    element.activeTabValue = "0";
    document.body.appendChild(element);
    const tabEle = element.shadowRoot.querySelector(".vloc-tab");
    expect(tabEle.getAttribute("data-index")).toBe("0");
  });
});
