import {
  createElement
} from "lwc";
import VlocityTextarea from "c/textarea";

describe("c-textarea", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-textarea", {
      is: VlocityTextarea
    });
    element.label = "Textarea";
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the label is set properly
   */
  it("Has label been set", () => {
    const element = createElement("c-textarea", {
      is: VlocityTextarea
    });
    element.label = "Textarea";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector("label");
    expect(ele.textContent).toBe("Textarea");
  });

  /**
   * Check if the value is set properly
   */
  it("Has value been set", () => {
    const element = createElement("c-textarea", {
      is: VlocityTextarea
    });
    element.value = "description";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector("textarea");
    expect(ele.value).toBe("description");
  });

  /**
   * Check if the min-length is set properly
   */
  it("Has min-length been set", () => {
    const element = createElement("c-textarea", {
      is: VlocityTextarea
    });
    element.label = "description";
    element.minLength = "30";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector("textarea");
    expect(ele.getAttribute("minlength")).toBe("30");
  });

  /**
   * Check if the min-length is set properly
   */
  it("Has max-length been set", () => {
    const element = createElement("c-textarea", {
      is: VlocityTextarea
    });
    element.label = "description";
    element.maxLength = "30";
    document.body.appendChild(element);
    const ele = element.shadowRoot.querySelector("textarea");
    expect(ele.getAttribute("maxlength")).toBe("30");
  });

});
