import { createElement } from "lwc";
import VlocityToast from "c/toast";

describe("c-toast", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-toast", {
      is: VlocityToast
    });
    element.title = "Test";
    element.message = "test toast";
    element.iconUrl = "/slds";
    element.show();
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Check if theme is set", () => {
    const element = createElement("c-toast", {
      is: VlocityToast
    });
    element.theme = "slds";
    element.title = "Test";
    element.message = "test toast";
    element.iconUrl = "/slds";
    element.show();
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector("div");
    let index = button.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /* Check if the variant disabled */
  it("Check If styletype is set", () => {
    const element = createElement("c-toast", {
      is: VlocityToast
    });
    element.theme = "slds";
    element.title = "Test";
    element.message = "test toast";
    element.iconUrl = "/slds";
    element.styletype = "success";
    element.show();
    document.body.appendChild(element);
    const button = element.shadowRoot.querySelector(".slds-notify");
    let index =
      button.className.indexOf("slds-theme_success") !== -1 ? true : false;
    expect(index).toBe(true);
  });
});
