import { createElement } from "lwc";
import Tree from "c/treeItem";

describe("c-tree-item", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("creates tree item", () => {
    const element = createElement("c-tree-item", {
      is: Tree
    });
    element.items = [
      {
        label: "Go to Record 1",
        href: "#record1",
        items: [],
        expanded: true
      },
      {
        label: "Go to Record 2",
        href: "#record2",
        items: [],
        expanded: true
      },
      {
        label: "Go to Record 3",
        href: "#record3",
        items: [],
        expanded: true
      }
    ];
    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      const ele = element.shadowRoot.querySelectorAll("ul");
      expect(ele).toHaveLength(element.items.length);
    });
  });
});
