import { LightningElement } from "lwc";
import { parseParams } from "c/navigationUtils";

export default class VlocityBaseApp extends LightningElement {
  routeConfig = new Map([
    ["*", { title: "Page Not Found", load: () => import("c/errorView") }]
  ]);

  redirects = new Map();

  get params() {
    return parseParams(window.location.search);
  }

  get router() {
    if (!this._router)
      this._router = this.template.querySelector("[data-component-router]");
    return this._router;
  }

  /**
   * @param {RouteChangeEvent} evt
   */
  routeChange(evt) {
    this.router.routeChange(evt);
  }
}
