import { LightningElement, api } from "lwc";
/**
 * @module c/wizardItem
 * @typicalname WizardItem
 * @example
 * ```
 * <c-wizard-item label="Step1">
 *  <div slot="wizarditemcontent">
 *      ....
 *  </div>
 * </c-wizard-item>
 * ```
 */
export default class WizardItem extends LightningElement {
  _wrapperClass = "";

  /**
   * @type {String} - Sets the id of the wizardItem. Should be unique per item.
   * @scope api (public)
   */
  @api wizardItemId;

  /**
   * @type {String} - This is the label of the item. This is required. Its also the identifier and needs to be unique.
   * @scope api (public)
   */
  @api label;

  /**
   * @type {String} - Wrapperclass to be put on the div wrapping the content.
   * @scope api (public)
   */
  @api get wizardItemWrapperClass() {
    return this._wrapperClass;
  }
  set wizardItemWrapperClass(val) {
    this._wrapperClass = val;
  }

  connectedCallback() {
    this.classList.add("vlocity-wizard-item");
  }
}
