import { createElement } from 'lwc';
import { delay } from 'c/asyncUtils';

import OmniscriptCheckbox from 'c/omniscriptCheckbox';

describe('c-omniscript-checkbox', () => {
    const mockGetRecord = require('./mock.json');
    let element = null;

    beforeEach(() => {
        element = createElement('c-omniscript-checkbox', {
            is: OmniscriptCheckbox,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'lightning';
        element.scriptHeaderDef = { allCustomLabels: {} };
    });

    afterEach(() => {
        while (document.body.firstChild) {
            document.body.removeChild(document.body.firstChild);
        }
    });

    it('renders DOM elements', () => {
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
    });

    it('Test value is change c-masked-input', () => {
        document.body.appendChild(element);
        return new Promise(resolve => {
            const input = element.shadowRoot.querySelector('c-input');
            element.addEventListener('omniaggregate', event => {
                expect(event.detail.data).toBe(true);
                resolve();
            });
            input.checked = true;
            input.dispatchEvent(new CustomEvent('change', {}));
        });
    });

    it('can repeat', () => {
        let jsonDefCopy = JSON.parse(JSON.stringify(mockGetRecord));
        jsonDefCopy.propSetMap.repeat = true;
        element.jsonDef = jsonDefCopy;
        document.body.appendChild(element);
        const addButton = element.shadowRoot.querySelector('.omni-repeat-button-group > button');
        return new Promise(resolve => {
            element.addEventListener('omnirepeat', () => resolve());
            addButton.click();
        });
    });

    // eslint-disable-next-line @lwc/lwc/no-async-await
    it('should only set booleans from an api', async () => {
        document.body.appendChild(element);
        const input = element.shadowRoot.querySelector('c-input');

        element.applyCallResp('hello', true);
        await delay(0);
        expect(input.checked).toBe(false);

        element.applyCallResp(true, true);
        await delay(0);
        expect(input.checked).toBe(true);
    });

    it('should clear custom validity on focusout', () => {
        document.body.appendChild(element);
        element.applyCallResp('custom error', false, true);
        expect(element.reportValidity()).toBe(false);

        element.focus();
        expect(element.reportValidity()).toBe(false);

        const input = element.shadowRoot.querySelector('c-input');
        input.dispatchEvent(new CustomEvent('focusout', { bubbles: true }));
        expect(element.reportValidity()).toBe(true);
    });

    it('should set checked after clearing custom validity', () => {
        document.body.appendChild(element);
        element.applyCallResp('custom error', false, true);
        element.focus();

        const input = element.shadowRoot.querySelector('c-input');

        return new Promise(resolve => {
            element.addEventListener('omniaggregate', event => {
                expect(event.detail.data).toBe(true);
                resolve();
            });
            input.checked = true;
            input.dispatchEvent(new CustomEvent('focusout', { bubbles: true }));
        });
    });
});
