import { createElement } from 'lwc';
import OmniscriptDisclosure from 'c/omniscriptDisclosure';

describe('c-omniscript-disclosure', () => {
    const mockGetRecord = require('./mock.json');
    let element = null;

    beforeEach(() => {
        element = createElement('c-omniscript-disclosure', {
            is: OmniscriptDisclosure,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.scriptHeaderDef = { allCustomLabels: {} };
    });

    afterEach(() => {
        while (document.body.firstChild) {
            document.body.removeChild(document.body.firstChild);
        }
    });

    it('should render DOM in lightning mode', () => {
        element.layout = 'lightning';
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
        expect(element.jsonDef.response).toBe(false);
        expect(element.shadowRoot.querySelector('.slds-box')).toBeTruthy();
    });

    it('should render DOM in newport mode', () => {
        element.layout = 'newport';
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
        expect(element.jsonDef.response).toBe(false);
        expect(element.shadowRoot.querySelector('.nds-box')).toBeTruthy();
    });

    it('should emit omniaggregate when disclosure is changed', () => {
        element.layout = 'lightning';
        document.body.appendChild(element);

        return new Promise(resolve => {
            const input = element.shadowRoot.querySelector('c-input');
            element.addEventListener('omniaggregate', event => {
                expect(event.detail.data).toBe(true);
                resolve();
            });
            input.checked = true;
            input.dispatchEvent(new CustomEvent('change', {}));
        });
    });

    it('should clear custom validity on focusout', () => {
        element.layout = 'lightning';
        document.body.appendChild(element);
        element.applyCallResp('custom error', false, true);
        expect(element.reportValidity()).toBe(false);

        element.focus();
        expect(element.reportValidity()).toBe(false);

        const input = element.shadowRoot.querySelector('c-input');
        input.dispatchEvent(new CustomEvent('focusout', { bubbles: true }));
        expect(element.reportValidity()).toBe(true);
    });

    it('should set disclosure to be checked after clearing custom validity on focusout', () => {
        element.layout = 'lightning';
        document.body.appendChild(element);
        element.applyCallResp('custom error', false, true);
        element.focus();

        const input = element.shadowRoot.querySelector('c-input');

        return new Promise(resolve => {
            element.addEventListener('omniaggregate', event => {
                expect(event.detail.data).toBe(true);
                resolve();
            });
            input.checked = true;
            input.dispatchEvent(new CustomEvent('focusout', { bubbles: true }));
        });
    });
});
