import { createElement } from 'lwc';

import OmniscriptEmailAction from 'c/omniscriptEmailAction';
import { delay } from 'c/asyncUtils';
import * as Link from 'c/omniscriptRestApi';

describe('c-omniscript-email-action', () => {
    const mockGetRecord = require('./mock.json');
    const successResponse = require('./response.json');
    let element = null;
    let success = null;

    beforeEach(() => {
        element = createElement('c-omniscript-email-action', {
            is: OmniscriptEmailAction,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'lightning';
        element.scriptHeaderDef = { allCustomLabels: {}, propSetMap: {} };

        success = jest.spyOn(Link, 'GenericInvoke2NoCont');
    });
    afterEach(() => {
        while (document.body.firstChild) {
            document.body.removeChild(document.body.firstChild);
        }
        jest.restoreAllMocks();
    });

    it('renders DOM elements in lightning mode', () => {
        element.layout = 'lightning';
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
        expect(element.shadowRoot.querySelector('.slds-is-relative.slds-p-top_xx-small')).toBeTruthy();
    });

    it('renders DOM elements in newport mode', () => {
        element.layout = 'newport';
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
        expect(element.shadowRoot.querySelector('.nds-is-relative.nds-p-around_x-small.nds-m-bottom_x-small')).toBeTruthy();
    });

    // eslint-disable-next-line @lwc/lwc/no-async-await
    it('Should show a spinner after clicking the button', async () => {
        expect.assertions(2);
        document.body.appendChild(element);

        const input = element.shadowRoot.querySelector('c-button');
        await delay(0);
        expect(element.shadowRoot.querySelector('c-spinner')).toBeNull();
        input.click();
        return Promise.resolve().then(() => {
            expect(element.shadowRoot.querySelector('c-spinner')).not.toBeNull();
        });
    });

    // eslint-disable-next-line @lwc/lwc/no-async-await
    it('shows the call parameters corresponding to the email list provided when clicking email action', async () => {
        expect.assertions(2);
        let newList = ['test@salesforce.org', [{ string: 'test2@salesforce.org' }, 'test3@salesforce.org']];

        let jsonDefCopy = JSON.parse(JSON.stringify(mockGetRecord));
        jsonDefCopy.propSetMap.emailInformation.toAddressList = newList;
        element.jsonDef = jsonDefCopy;

        document.body.appendChild(element);

        await delay(0);
        const input = element.shadowRoot.querySelector('c-button');
        success.mockImplementation(() => Promise.resolve(JSON.stringify(successResponse)));

        input.dispatchEvent(new CustomEvent('click', {}));

        let options = JSON.parse(success.mock.calls[0][0].options);
        expect(options.toAddressList).toHaveLength(3);
        expect(options.toAddressList[1]).toBe('test2@salesforce.org');
    });

    // eslint-disable-next-line @lwc/lwc/no-async-await
    it('Should have apiResponse when clicking email action with valid responses', async () => {
        expect.assertions(1);
        document.body.appendChild(element);

        const input = element.shadowRoot.querySelector('c-button');
        await delay(0);

        success.mockImplementation(() => Promise.resolve(JSON.stringify(successResponse)));
        return new Promise(resolve => {
            element.addEventListener('omniactionbtn', event => {
                expect(event.detail.apiResponse).toBeTruthy();
                resolve();
            });

            input.dispatchEvent(new CustomEvent('click', {}));
        });
    });
});
