import DOMPurify from 'dompurify';
import { api, LightningElement, track } from 'lwc';
import { config } from './omniscriptFormattedRichTextConfig';

/**
 * OmniScript Formatted Rich Text, extends LightningElement
 */
export default class OmniscriptFormatedRichText extends LightningElement {
    _initialized = false;
    @track _value;

    @api set value(v) {
        this._value = v;
        this.update();
    }

    get value() {
        return this._value;
    }

    @api disableLinkify;

    renderedCallback() {
        this._initialized = true;
        this.update();
    }

    update() {
        if (!this._initialized) return;
        const div = this.template.querySelector('div');
        if (div && this.value) {
            // TODO: Move this to resources/lightning-formatted-text
            div.innerHTML = DOMPurify.sanitize(this.value, config);
        }
    }
}
