import { createElement } from 'lwc';
import OmniscriptRadio from 'c/omniscriptRadio';

describe('c-omniscript-radio', () => {
    const mockGetRecord = require('./mock.json');
    let element = null;

    beforeEach(() => {
        element = createElement('c-omniscript-radio', {
            is: OmniscriptRadio,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.scriptHeaderDef = { allCustomLabels: {}, propSetMap: {} };
    });

    afterEach(() => {
        while (document.body.firstChild) {
            document.body.removeChild(document.body.firstChild);
        }
    });

    it('should render DOM elements in Newport mode', () => {
        const mock = JSON.parse(JSON.stringify(mockGetRecord));
        mock.propSetMap.repeat = true;
        element.jsonDef = mock;

        element.layout = 'newport';
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
        expect(element.shadowRoot.querySelector('.omni-repeat-button-group.nds-m-bottom_xx-small')).toBeTruthy();
    });

    it('should render DOM elements in Lightning mode', () => {
        const mock = JSON.parse(JSON.stringify(mockGetRecord));
        mock.propSetMap.repeat = true;
        element.jsonDef = mock;

        element.layout = 'lightning';
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
        expect(element.shadowRoot.querySelector('.omni-repeat-button-group.slds-button-group.slds-p-right_x-small')).toBeTruthy();
    });

    it('should emit an omniaggregate signal on change', () => {
        element.layout = 'newport';
        element.options = mockGetRecord.propSetMap.options;
        document.body.appendChild(element);
        return new Promise(resolve => {
            const input = element.shadowRoot.querySelector('c-radio-image-group');
            input.options = element.options;
            element.addEventListener('omniaggregate', event => {
                expect(event.detail.data).toBe('omniscript');
                resolve();
            });
            input.value = 'omniscript';
            input.dispatchEvent(new CustomEvent('change', {}));
        });
    });

    it('should emit an omniautoadvance signal on change if autoadvance is enabled', () => {
        const mock = JSON.parse(JSON.stringify(mockGetRecord));
        mock.propSetMap.options = [
            {
                autoAdv: 'next',
                value: 'Omniscript',
                name: 'Omniscript',
            },
        ];
        element.jsonDef = mock;
        element.layout = 'lightning';
        document.body.appendChild(element);

        element.focus();
        return new Promise(resolve => {
            const input = element.shadowRoot.querySelector('c-radio-image-group');

            element.addEventListener('omniautoadvance', event => {
                expect(event.detail.moveToStep).toBe('next');
                resolve();
            });
            input.value = 'Omniscript';
            input.dispatchEvent(new CustomEvent('change', {}));
        });
    });

    it('should emit omnirepeat in Lightning mode when repeat button is clicked', () => {
        element.layout = 'lightning';
        let jsonDefCopy = JSON.parse(JSON.stringify(mockGetRecord));
        jsonDefCopy.propSetMap.repeat = true;
        element.jsonDef = jsonDefCopy;
        document.body.appendChild(element);
        const addButton = element.shadowRoot.querySelector('.omni-repeat-button-group > button');
        return new Promise(resolve => {
            element.addEventListener('omnirepeat', () => resolve());
            addButton.click();
        });
    });

    it('should emit omnirepeat in Newport mode when repeat button is clicked', () => {
        element.layout = 'newport';
        let jsonDefCopy = JSON.parse(JSON.stringify(mockGetRecord));
        jsonDefCopy.propSetMap.repeat = true;
        element.jsonDef = jsonDefCopy;
        document.body.appendChild(element);
        const addButton = element.shadowRoot.querySelector('.omni-repeat-button-group > button');
        return new Promise(resolve => {
            element.addEventListener('omnirepeat', () => resolve());
            addButton.click();
        });
    });
});
