import { createElement } from 'lwc';

import OmniscriptSelect from 'c/omniscriptSelect';
import { delay } from 'c/asyncUtils';

describe('c-omniscript-select', () => {
    const mockGetRecord = require('./mock.json');
    let element = null;

    beforeEach(() => {
        element = createElement('c-omniscript-select', {
            is: OmniscriptSelect,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'newport';
        element.scriptHeaderDef = { allCustomLabels: {} };
    });

    afterEach(() => {
        while (document.body.firstChild) {
            document.body.removeChild(document.body.firstChild);
        }
        element = null;
    });

    it('renders DOM elements', () => {
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
    });

    // eslint-disable-next-line @lwc/lwc/no-async-await
    it('Test value is change', async () => {
        document.body.appendChild(element);

        const input = element.shadowRoot.querySelector('c-combobox');
        input.value = 'Omniscript';
        await delay(0);

        return new Promise(resolve => {
            element.addEventListener('omniaggregate', event => {
                expect(event.detail.data).toBe('Omniscript');
                resolve();
            });
            input.dispatchEvent(new CustomEvent('change', {}));
        });
    });

    it('Test if label is set', () => {
        document.body.appendChild(element);
        const input = element.shadowRoot.querySelector('c-combobox');
        expect(input.label).toBe(mockGetRecord.propSetMap.label);
    });

    // eslint-disable-next-line @lwc/lwc/no-async-await
    it('Test omniautoadvance', async () => {
        const mock = JSON.parse(JSON.stringify(mockGetRecord));
        mock.propSetMap.options = [
            {
                autoAdv: 'next',
                value: 'Omniscript',
                name: 'Omniscript',
            },
        ];
        element.jsonDef = mock;
        document.body.appendChild(element);

        const input = element.shadowRoot.querySelector('c-combobox');
        input.value = 'Omniscript';
        await delay(0);

        return new Promise(resolve => {
            element.addEventListener('omniautoadvance', event => {
                expect(event.detail.moveToStep).toBe('next');
                resolve();
            });
            input.dispatchEvent(new CustomEvent('change', {}));
        });
    });
});
