import { createElement } from 'lwc';

import OmniscriptTextarea from 'c/omniscriptTextarea';

describe('c-omniscript-textarea', () => {
    const mockGetRecord = require('./mocks/getRecord.json');
    const mockScriptHeaderDef = require('./mocks/scriptHeaderDef.json');
    afterEach(() => {
        while (document.body.firstChild) {
            document.body.removeChild(document.body.firstChild);
        }
    });
    it('renders DOM elements', () => {
        const element = createElement('c-omniscript-textarea', {
            is: OmniscriptTextarea,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'newport';
        element.scriptHeaderDef = mockScriptHeaderDef;
        document.body.appendChild(element);
        expect(element.shadowRoot.children).not.toHaveLength(0);
    });

    it('Test value is change', () => {
        const element = createElement('c-omniscript-textarea', {
            is: OmniscriptTextarea,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'newport';
        element.scriptHeaderDef = mockScriptHeaderDef;
        document.body.appendChild(element);

        return new Promise(resolve => {
            const input = element.shadowRoot.querySelector('c-textarea');
            element.addEventListener('omniaggregate', event => {
                expect(event.detail.data).toBe('testing123');
                resolve();
            });
            input.value = 'testing123';
            input.dispatchEvent(new CustomEvent('blur', {}));
        });
    });

    it('Test if label is set', () => {
        const element = createElement('c-omniscript-textarea', {
            is: OmniscriptTextarea,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'newport';
        element.scriptHeaderDef = mockScriptHeaderDef;
        document.body.appendChild(element);
        const input = element.shadowRoot.querySelector('c-textarea');
        expect(input.label).toBe(mockGetRecord.propSetMap.label);
    });

    it('Test min-length been set', () => {
        const element = createElement('c-omniscript-textarea', {
            is: OmniscriptTextarea,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'newport';
        element.scriptHeaderDef = mockScriptHeaderDef;
        document.body.appendChild(element);
        const input = element.shadowRoot.querySelector('c-textarea');
        expect(input.minLength).toBe(mockGetRecord.propSetMap.minLength);
    });

    it('Test max-length been set', () => {
        const element = createElement('c-omniscript-textarea', {
            is: OmniscriptTextarea,
        });
        element.key = mockGetRecord.name;
        element.jsonDef = mockGetRecord;
        element.dataOmniKey = mockGetRecord.index;
        element.jsonData = null;
        element.layout = 'newport';
        element.scriptHeaderDef = mockScriptHeaderDef;
        document.body.appendChild(element);
        const input = element.shadowRoot.querySelector('c-textarea');
        expect(input.maxLength).toBe(mockGetRecord.propSetMap.maxLength);
    });
});
