/**
 * This file is used to build a custom version of Day.JS that is compiled and optimized with
 * all the necessary functions we need in the Expression Engine to reproducd Moment.js
 * features.
 */
import dayjs from 'dayjs/esm';
import advancedFormat from 'dayjs/esm/plugin/advancedFormat';
import calendar from 'dayjs/esm/plugin/calendar';
import customParseFormat from 'dayjs/esm/plugin/customParseFormat';
import dayOfYear from 'dayjs/esm/plugin/dayOfYear';
import isBetween from 'dayjs/esm/plugin/isBetween';
import isLeapYear from 'dayjs/esm/plugin/isLeapYear';
import isMoment from 'dayjs/esm/plugin/isMoment';
import isSameOrAfter from 'dayjs/esm/plugin/isSameOrAfter';
import isSameOrBefore from 'dayjs/esm/plugin/isSameOrBefore';
import localeData from 'dayjs/esm/plugin/localeData';
import localizedFormat from 'dayjs/esm/plugin/localizedFormat';
import minMax from 'dayjs/esm/plugin/minMax';
import timezone from 'dayjs/esm/plugin/timezone';
import utc from 'dayjs/esm/plugin/utc';
import weekday from 'dayjs/esm/plugin/weekday';
import weekOfYear from 'dayjs/esm/plugin/weekOfYear';
import weekYear from 'dayjs/esm/plugin/utc';

dayjs.extend(advancedFormat);
dayjs.extend(calendar);
dayjs.extend(customParseFormat);
dayjs.extend(dayOfYear);
dayjs.extend(isBetween);
dayjs.extend(isLeapYear);
dayjs.extend(isMoment);
dayjs.extend(isSameOrAfter);
dayjs.extend(isSameOrBefore);
dayjs.extend(localeData);
dayjs.extend(localizedFormat);
dayjs.extend(minMax);
dayjs.extend(timezone);
dayjs.extend(utc);
dayjs.extend(weekday);
dayjs.extend(weekOfYear);
dayjs.extend(weekYear);

export default dayjs;
