let _connection;


/**
 * Returns the current connection instance
 * @returns {object}
 */
export function getConnection() {
    return _connection;
}

/**
 * Manually sets a connection
 * @param {OmniscriptConnection} connection
 */
export function setConnection(connection) {
    if (connection === undefined || connection === null || typeof connection.request !== 'function') {
        throw new Error('The connection object is invalid. Please verify you have a request method.');
    }

    _connection = connection;
}