import { createElement } from "lwc";
import formattedDateTime from "lightning/formattedDateTime";

describe("lightning-formattedDateTime", () => {
  it("renders DOM elements", () => {
    const element = createElement("lightning-formatted-date-time", {
      is: formattedDateTime
    });
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  it("renders formattedDateTime component", () => {
    const element = createElement("lightning-formatted-date-time", {
      is: formattedDateTime
    });

    document.body.appendChild(element);
    return Promise.resolve().then(() => {
      // eslint-disable-next-line @lwc/lwc/no-async-operation
      window.setTimeout(function() {
        
        const ele = element.shadowRoot.querySelector("lightning-formatted-date-time");
       
        expect(ele.length()).toBe(1);
      });
    });
  });
  
});
