interface LatLngLocation {
  /**
   * Number cast as a string representing a coordinates latitudinal parallel.
   */
  Latitude: string;
  /**
   * Number cast as a string representing a coordinates longitudinal meridian.
   */
  Longitude: string;
}

interface AddressLocation {
  City: string;
  Country: string;
  PostalCode: string;
  State: string;
  Street: string;
}

interface MapMarker {
  /**
   * Address elements (City, Country, PostalCode, State, and Street) to be geocoded,
   * or a set of latitude and longitude coordinates. If you specify address elements
   * and coordinates for one location, the map uses the coordinates. To support reliable geocoding of addresses,
   * if you specify Street, you must also specify at least one of City, Country, PostalCode or State.
   */
  location: LatLngLocation | AddressLocation;

  /**
   * An optional unique identifier for a marker. When you select a marker,
   * its value is assigned to the selected-marker-value attribute.
   */
  value?: string;

  /**
   * The location title for the marker, displayed in the location list and in the info window when you click a marker.
   */
  title?: string;

  /**
   * The information displayed in the info window when you click a marker or location title.
   */
  description?: string;

  /**
   * The icon that's displayed next to the location title in the list.
   * Only Lightning Design System icons are supported. Custom marker icons are currently not supported.
   * The default is standard:location. For more information, see Displaying Multiple Addresses and a Title.
   */
  icon?: string;
}
