export const DEFAULT_CENTER = { location: { Latitude: 0, Longitude: 180 } };
export const DEFAULT_ZOOM = 1;

export function lwcToGoogleLatLng(sfLatLang) {
  return new google.maps.LatLng(sfLatLang.Latitude, sfLatLang.Longitude);
}
/**
 *
 * @param {MapMarker[]} mapMarkers
 * @param {'Latitude' | 'Longitude'} property
 */
export function getMaxBounds(mapMarkers, property) {
  const maxVal = mapMarkers.reduce(
    // eslint-disable-next-line no-confusing-arrow
    (max, marker) =>
      marker.location[property] > max ? marker.location[property] : max,
    mapMarkers[0].location[property]
  );

  return Number(maxVal);
}

/**
 *
 * @param {MapMarker[]} mapMarkers
 * @param {'Latitude' | 'Longitude'} property
 */
export function getMinBounds(mapMarkers, property) {
  const minVal = mapMarkers.reduce(
    // eslint-disable-next-line no-confusing-arrow
    (min, marker) =>
      marker.location[property] < min ? marker.location[property] : min,
    mapMarkers[0].location[property]
  );

  return Number(minVal);
}

