interface PageReference {
  type: "standard__component" | "standard__webPage";
  attributes: any;
  state: any;
}

interface ComponentPageReference extends PageReferece {
  type: "standard__component";
  attributes: { componentName: string };
}

interface WebPageReference extends PageReferece {
  type: "standard__webPage";
  attributes: { url: string };
}
